/* Procedure structure for procedure `calFunded` */

/*!50003 DROP PROCEDURE IF EXISTS  `calFunded` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ecc2`@`%` PROCEDURE `calFunded`(IN `requisiteId` int,IN `value` decimal(14,2),IN `isVerified` tinyint)
BEGIN
SELECT funded, total_value INTO @funded, @totalValue FROM ic_project_requisite WHERE id = requisiteId;

IF (@isVerified = 1) THEN
SELECT @funded + `Value` into @newFunded;

IF (@totalValue <= @newFunded) THEN
UPDATE ic_project_requisite SET is_funded = 1, funded = @newFunded WHERE id = requisiteId;
ELSE
UPDATE ic_project_requisite SET is_funded = 0, funded = @newFunded WHERE id = requisiteId;
END IF;

ELSEIF (isVerified = 0) THEN
SELECT @funded - `Value` into @newFunded;

IF (@totalValue <= @newFunded) THEN
UPDATE ic_project_requisite SET is_funded = 1, funded = @newFunded WHERE id = requisiteId;
ELSE
UPDATE ic_project_requisite SET is_funded = 0, funded = @newFunded WHERE id = requisiteId;
END IF;

END IF;

END */$$
DELIMITER ;

/* Procedure structure for procedure `calFundedMaterial` */

/*!50003 DROP PROCEDURE IF EXISTS  `calFundedMaterial` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ecc2`@`%` PROCEDURE `calFundedMaterial`(IN `requisiteId` int,IN `newAvailableWorth` decimal,IN `oldAvailableWorth` decimal)
BEGIN
SELECT funded, total_value INTO @funded, @totalValue FROM ic_project_requisite WHERE id = requisiteId;
SELECT @funded + `newAvailableWorth` - `oldAvailableWorth` into @newFunded;

IF (@totalValue <= @newFunded) THEN
UPDATE ic_project_requisite SET is_funded = 1, funded = @newFunded WHERE id = requisiteId;
ELSE
UPDATE ic_project_requisite SET is_funded = 0, funded = @newFunded WHERE id = requisiteId;
END IF;

END */$$
