DELIMITER $$

/*!50003 CREATE DEFINER=`ecc2`@`%` PROCEDURE `calFundingTotal`(IN `requisiteId` int, IN `newValue` dec(14,2), IN `oldValue` dec(14,2), IN `isDelete` tinyint)
BEGIN
IF (@isDelete = 0) THEN
SELECT total_value INTO @totalValue FROM ic_project_requisite WHERE id = requisiteId;
UPDATE ic_project_requisite SET total_value = @totalValue + `NewValue` -  `OldValue` WHERE id = requisiteId;
ELSEIF (@isDelete = 1) THEN
SELECT total_value INTO @totalValue FROM ic_project_requisite WHERE id = requisiteId;
UPDATE ic_project_requisite SET total_value = @totalValue - `OldValue` WHERE id = requisiteId;
END IF;
END */$$
