DELIMITER $$

/*!50003 CREATE DEFINER=`ecc2`@`%` PROCEDURE `calInitProgress`(IN `initiatorId` int, IN `isVerified` tinyint)
BEGIN
IF (isVerified = 1) THEN
	SELECT project_initiated_progress into @oldProgress FROM swt_users WHERE id = initiatorId;
	UPDATE swt_users SET project_initiated_progress = @oldProgress + 1 WHERE id = initiatorId;
ELSEIF (isVerified = 0) THEN
	SELECT project_initiated_progress into @oldProgress FROM swt_users WHERE id = initiatorId;
	UPDATE swt_users SET project_initiated_progress = @oldProgress - 1 WHERE id = initiatorId;
END IF;
END */$$
