/* Procedure structure for procedure `cronFailedProject` */

/*!50003 DROP PROCEDURE IF EXISTS  `cronFailedProject` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ecc2`@`%` PROCEDURE `cronFailedProject`()
BEGIN
  DECLARE done BOOLEAN DEFAULT FALSE;
  DECLARE _projetId INT UNSIGNED;
  DECLARE _requisiteId INT UNSIGNED;
  DECLARE cur CURSOR FOR SELECT project_id, id FROM ic_project_requisite WHERE funding_closed_time = DATE(NOW()) - INTERVAL 1 DAY AND counter_time = 1 AND is_funded = 0 AND is_approved = 1;
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done := 1;

  OPEN cur;

  failedProjectLoop: LOOP
    FETCH cur INTO _projetId, _requisiteId;
    IF done THEN
      LEAVE failedProjectLoop;
    END IF;
		UPDATE ic_project SET is_funded = 0 WHERE id = _projetId;

    SELECT inisiator_id INTO @inisiatorId FROM ic_project WHERE id = _projetId;
    SELECT project_initiated_progress, project_initiated_failed INTO @oldProgress, @oldFailed FROM swt_users WHERE id = @inisiatorId;
		UPDATE swt_users SET project_initiated_progress = @oldProgress - 1, project_initiated_failed = @oldFailed + 1 WHERE id = @inisiatorId;

		CALL setCancelVolunteer(_requisiteId);
		CALL setSponsoredFailed(_requisiteId);
		
  END LOOP failedProjectLoop;

  CLOSE cur;
END */$$
