DELIMITER $$

/*!50003 CREATE DEFINER=`ecc2`@`%` PROCEDURE `cronFailedRequisite`()
BEGIN
  DECLARE done BOOLEAN DEFAULT FALSE;
  DECLARE _projetId INT UNSIGNED;
