DELIMITER $$

/*!50003 CREATE DEFINER=`ecc2`@`%` PROCEDURE `startFirstRequisite`(IN `projectId` int, IN `verified` tinyint)
BEGIN
IF (verified = 1) THEN
SELECT id, funding_time INTO @fundingId, @fundingTime FROM ic_project_requisite WHERE (project_id = projectId AND counter_time = 1);
UPDATE ic_project_requisite SET is_approved = 1, funding_started_time = NOW(), funding_closed_time = INTERVAL @fundingTime MONTH + NOW() WHERE id = @fundingId;
ELSEIF (verified = 0) THEN
SELECT id INTO @fundingId FROM ic_project_requisite WHERE (project_id = projectId AND counter_time = 1);
UPDATE ic_project_requisite SET is_approved = 0, funding_started_time = NULL, funding_closed_time = NULL WHERE id = @fundingId;
END IF;
END */$$
